"use client";
import React, { useState } from "react";
import { moodBasedIngredients } from "../data/moodBasedIngredients";

const moodImages = {
  comfort:"data:image/jpeg;base64,", // pasta/comfort
  healthy:
    "data:image/jpeg;base64,", // salad/healthy
  spicy:
    "https://images.immediate.co.uk/production/volatile/sites/30/2018/06/One-pot-spiced-chicken-1763078.jpg?resize=1366,1503", // spicy curry
  dessert:"data:image/jpeg;base64,", // dessert,
savoury:"https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTFxBrIQCMJVaF9etGw7JZUQD-coTYn51denA&s"
}
export default function MoodRecipes() {
  const [selectedMood, setSelectedMood] = useState(null);
  const [meals, setMeals] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchMeals = async (mood) => {
    setSelectedMood(mood);
    setLoading(true);
    setError(null);

    const ingredients = moodBasedIngredients[mood];
    const randomIngredient =
      ingredients[Math.floor(Math.random() * ingredients.length)];

    try {
      const res = await fetch(
        `https://www.themealdb.com/api/json/v1/1/filter.php?i=${randomIngredient}`
      );
      const data = await res.json();

      if (data.meals) {
        setMeals(data.meals.slice(0, 6)); // show first 6
      } else {
        setError(`No recipes found for ${mood} mood.`);
      }
    } catch (err) {
      setError("Something went wrong. Please try again later.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <section className="bg-white py-16 px-6 md:px-20 text-center " id="mood">
      <h2 className="text-3xl font-semibold text-[#cf6c50] mb-4">
        Recipes by Mood 🍽️
      </h2>
      <p className="text-gray-700 mb-8">
        Choose your mood and discover recipes that match your vibe!
      </p>

      {/* Mood Buttons with Pictures */}
      <div className="flex flex-wrap justify-center gap-6 mb-10">
        {Object.keys(moodBasedIngredients).map((mood) => (
          <div
            key={mood}
            className="flex flex-col items-center cursor-pointer transition hover:scale-105"
            onClick={() => fetchMeals(mood)}
          >
            <div
              className={`relative w-30 h-30 md:w-50 md:h-50 rounded-full overflow-hidden border-4 transition ${
                selectedMood === mood
                  ? "border-[#cf6c50]"
                  : "border-[#feded4] hover:border-[#cf6c50]"
              }`}
            >
              <img
                src={moodImages[mood]}
                alt={mood}
                className="w-full h-full object-cover"
              />
            </div>
            <button
              className={`mt-3 px-4 py-1 rounded-full capitalize font-medium text-sm transition ${
                selectedMood === mood
                  ? "bg-[#cf6c50] text-white"
                  : "bg-[#feded4] text-[#cf6c50] hover:bg-[#cf6c50] hover:text-white"
              }`}
            >
              {mood}
            </button>
          </div>
        ))}
      </div>

      {/* Loading / Error */}
      {loading && <p className="text-gray-600 mb-6">Loading recipes...</p>}
      {error && <p className="text-red-600 mb-6">{error}</p>}

      {/* Meals Grid */}
      <div className="grid gap-6 md:grid-cols-3 sm:grid-cols-2">
        {meals.map((meal) => (
          <div
            key={meal.idMeal}
            className="bg-[#fcfbf4] rounded-xl shadow-md overflow-hidden hover:shadow-lg transition"
          >
            <a
              href={`https://www.themealdb.com/meal/${meal.idMeal}`}
              target="_blank"
              rel="noopener noreferrer"
              className="block"
            >
              <img
                src={meal.strMealThumb}
                alt={meal.strMeal}
                className="w-full h-48 object-cover rounded-lg cursor-pointer transition-transform duration-200 hover:scale-105"
              />
              <div className="p-4">
                <h3 className="text-lg font-medium text-gray-800 hover:text-[#cf6c50] transition-colors">
                  {meal.strMeal}
                </h3>
              </div>
            </a>
          </div>
        ))}
      </div>
    </section>
  );
}
